#import time

def rot13(string):
    array=[]
    for i in range(len(string)):
        ascii = ord(string[i])
        '''if ((ascii>=65 and ascii<=77) or (ascii>=97 and ascii<=109)):
            newascii = ascii + 13
        elif ((ascii>=78 and ascii<=90) or (ascii>=110 and ascii <=122)):
            newascii = ascii - 13
        else:
            newascii = ascii'''
        # branchless version for fun
        newascii = ((ascii>=65 and ascii<=77) or (ascii>=97 and ascii<=109))*(ascii + 13) + ((ascii>=78 and ascii<=90) or (ascii>=110 and ascii <=122))*(ascii-13) + (not string[i].isalpha())*(ascii)
        array.append(chr(newascii))
    return ''.join(array)
